// A game with crystals and a Random Number generator...
// Every Crystal needs to have a random number...
// A new Random Number should be generated by the Random Number Generator 
// every single time there is a Win or Loss to the 4 crystals.
// Each time a Crystal is clicked it should add with the previous result
// until we get the total score.
// If it is greater than the Random Result then we need to decrement the lost counter.
// If it is not equal then we increment a win counter.


var crystal = {

      Teal: {
            name: "Teal",
            value: 0
      },
      Yellow: {
            name: "Yellow",
            Value: 0
      },
      Orange: {
            name: "Orange",
            value: 0
      },
      Purple: {
            name: "Purple",
            value: 0
      },

};

var score = 0;
var obejectiveScore = 0;
var gamesWon = 0;
var gamesLost = 0;


var randomNumber = function (min, max) {
      return Math.floor(Math.random() * (max - min + 1)) + min;
};

var startgame = function () {

      score = 0;
      objectiveScore = randomNumber(25, 250);

      crystal.Orange.value = randomNumber(1, 25);
      crystal.Purple.value = randomNumber(1, 15);
      crystal.Teal.value = randomNumber(1, 10);
      crystal.Yellow.value = randomNumber(1, 25);

      $("#your-score").text(score);
      $("#objective-Score").text(objectiveScore);
      
};
var checkWin = function () {
      if (score > objectiveScore) {
            alert("You Lose!");

            gamesLost++;
            $("#loss-count").text(gamesLost);
            startgame();
      }
      else if (score === objectiveScore) {
            alert("You Win!!");
            gamesWon++;
            $("#win-count").text(gamesWon);
            startgame();
      }
};

var addValues = function (clickedCrystal) {
      score += clickedCrystal.value;

      $("your-score").text(score);

      checkWin();
};

startgame();

$("#Yellow").click(function () {
      addValues(crystal.Yellow);
});
$("#Orange").click(function () {
      addValues(crystal.Orange);
});
$("#Teal").click(function () {
      addValues(crystal.Teal);
});
$("#Purple").click(function () {
      addValues(crystal.Purple);
});

























